import "./App.css";
import { useState } from "react";
import DisplayMessage from "./components/DisplayMessage";

function App() {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const pass = document.querySelectorAll(".pass");

  const inputUserName = (e) => {
    setUserName(e.target.value);
    console.log(userName);
  };

  const inputPassword = (e) => {
    setPassword(e.target.value);
    console.log(password);
  };

  const submitButton = () => {
    if (userName) {
      alert(`Welcome ${userName}! You have successfully registered your account.`);
    }
  };

  const showPassword = () => {pass.forEach((pass) => {
    if (pass.type === "password"){
      pass.type = "text";
    } else {
      pass.type = "password";
    }
  })}


  return (
    <div className="App">
      <form>
        <h1>Account Registration</h1>
        <p>
          Username:{" "}
          <input type="text" value={userName} onChange={inputUserName} required></input>
        </p>
        <label value="Please"></label>
        <p>
          Password:{" "}
          <input type="password" className="pass" value={password} onChange={inputPassword} required></input>
        </p>
        <p>
          Confirm Password: <input type="password" className="pass" required></input>
        </p>
        Show Password:
        <input type="checkbox" onClick={showPassword}></input>
        <button onClick={submitButton}>Submit</button>
      </form>
    </div>
  );
}

export default App;
