import "./App.css";
import { useState } from "react";
import DisplayMessage from "./components/DisplayMessage";

function App() {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");

  const inputUserName = (e) => {
    setUserName(e.target.value);
    console.log(userName);
  };

  const inputPassword = (e) => {
    setPassword(e.target.value);
    console.log(password);
  };

  const submitButton = () => {
    if (!userName) {
      <p>Please enter a username</p>;
    }
    alert(`Welcome ${userName}! You have successfully registered your account.`);
    setUserName("");
  };

  return (
    <div className="App">
      <form>
        <h1>Account Registration</h1>
        <p>
          Username:{" "}
          <input type="text" value={userName} onChange={inputUserName}></input>
        </p>
        <label value="Please"></label>
        <p>
          Password:{" "}
          <input type="text" value={password} onChange={inputPassword}></input>
        </p>
        <p>
          Confirm Password: <input type="text"></input>
        </p>
        <button onClick={submitButton}>Submit</button>
      </form>
    </div>
  );
}

export default App;
